//OK ECRAN

import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect, useRef} from 'react';
import { StyleSheet, Text, ImageBackground, Button, TouchableOpacity, View} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { Card, ListItem, CheckBox } from 'react-native-elements'
import AsyncStorage from '@react-native-async-storage/async-storage';
//import { Container, Header, Content, CheckBox, Body } from 'native-base'


export default function App() {


  const [checkedBox1, setCheckedBox1] = useState(false);
  const [checkedBox2, setCheckedBox2] = useState(false);
  const [checkedBox3, setCheckedBox3] = useState(false);
  const [checkedBox4, setCheckedBox4] = useState(false);

 
  var handleSubmit1 = () => {
    setCheckedBox1(true);
    if(checkedBox1) {
      setCheckedBox1(false);
      AsyncStorage.setItem("ingredient1", checkedBox1)
    }
   }

   var handleSubmit2 = () => {
     setCheckedBox2(true);
     if(checkedBox2) {
       setCheckedBox2(false);
       AsyncStorage.setItem("ingredient2", checkedBox2)
     }
    }


    var handleSubmit3 = () => {
      setCheckedBox3(true);
      if(checkedBox3) {
        setCheckedBox3(false);
        AsyncStorage.setItem("ingredient3", checkedBox3)
      }
     }
  
  
     var handleSubmit4 = () => {
       setCheckedBox4(true);
       if(checkedBox4) {
         setCheckedBox4(false);
         AsyncStorage.setItem("ingredient4", checkedBox4)
       }
      }
      useEffect(() => {
        checkedBox1, checkedBox2, checkedBox3, checkedBox4

    }, []);

  return (
    <ImageBackground source={require('../assets/002.png')}style={{ height: 800,  alignItems: 'start'}}> 
    <View style={{alignItems: 'start', marginTop: 40}}>
   
        <Card style={{marginTop: 20}}>
              <Text style={{fontSize: 40}}> Semaine 1</Text>
          <ListItem style={{alignItems: 'start', marginBottom: -34 }}>
            <CheckBox 
            checked={checkedBox1}
            checkedColor="#F475BB"
            onPress={() => handleSubmit1()}/>
              <Text>Huile d'olive</Text>      
          </ListItem >
          <ListItem style={{alignItems: 'start', marginBottom: -34}}>
            <CheckBox 
             checked={checkedBox2}
             checkedColor="#F475BB"
             onPress={() => handleSubmit2()}
            />
              <Text>Beurre de karité</Text>         
          </ListItem>
          <ListItem style={{alignItems: 'start', marginBottom: -34}}>
            <CheckBox 
             checked={checkedBox3}
             checkedColor="#F475BB"
             onPress={() => handleSubmit3()}/>
              <Text>Lait d'avoine</Text>         
          </ListItem>
          <ListItem style={{alignItems: 'start', marginBottom: -10}}>
            <CheckBox 
             checked={checkedBox4}
             checkedColor="#F475BB"
             onPress={() => handleSubmit4()}
            />
              <Text>Avocat</Text>         
          </ListItem>
        </Card>
   </View>
  </ImageBackground>

  );
}

const styles = StyleSheet.create({
   container: {
     flex: 1,
     alignItems: 'center',
     justifyContent: 'flex-start',
   },
});

const stylesb = StyleSheet.create({
  button: {
    color: 'red'
  },
});
